DROP DATABASE IF EXISTS sorting;
CREATE DATABASE sorting;

\c sorting;

CREATE TABLE scores (
  ID SERIAL PRIMARY KEY,
  gryf FLOAT(4),
  slyt FLOAT(4),
  huff FLOAT(4),
  rave FLOAT(4)
);

CREATE TABLE hat (
  ID SERIAL PRIMARY KEY,
  answer VARCHAR,
  gryf FLOAT(4),
  slyt FLOAT(4),
  huff FLOAT(4),
  rave FLOAT(4)
);

